navigation-skins.css:
/********************************
	-	FLAT LIGHT BUTTONS -
*********************************/
.the-landscaper .esg-filter-wrapper {
  width: 100%;
}
.the-landscaper .navigationbuttons,
.the-landscaper .esg-pagination,
.the-landscaper .esg-filters{
  text-transform:uppercase;
  text-align: center;
}

.the-landscaper .esg-filterbutton {
  position: relative;
}
.the-landscaper .esg-loadmore-wrapper {
  margin-top: 30px;
}


  .the-landscaper .esg-filterbutton,
  .the-landscaper .esg-sortbutton,
  .the-landscaper .esg-cartbutton {
    display: block;
    padding: 14px 25px;
    color: #999999;
    font-family: "Roboto", sans-serif;
    font-size: 15px;
    font-weight: bold;
    z-index: 2;
    text-transform: none;
    cursor: pointer;
    background-color: #f5f5f5;
    border-radius: 1px;
    transition: all 100ms ease-in;
  }

.the-landscaper .esg-navigationbutton.esg-left,
.the-landscaper .esg-navigationbutton.esg-right {
  border-color: #50524B;
}

@media(min-width: 992px) {
  .the-landscaper .esg-filterbutton,
  .the-landscaper .esg-sortbutton,
  .the-landscaper .esg-cartbutton {
    position: relative;
    display: inline-block;
    margin-right: 7px;
  }
  .the-landscaper .esg-filter-checked {
    padding: 1px 3px;
    color: #999999;
    background: #f9f9f9;
    margin-left: 7px;
    font-size: 9px;
    font-weight: 300;
    line-height: 9px;
    vertical-align: middle;
  }
}


.the-landscaper .esg-filterbutton.selected,
.the-landscaper .esg-filterbutton:hover {
  background-color: #A2C046;
  color: #ffffff;
  border-color: #A2C046;
}
.the-landscaper .esg-navigationbutton {
  display: inline-block;
  bottom: 22px;
  padding: 12px 13px;
  font-family: "Roboto", sans-serif;
  font-weight: bold;
  cursor: pointer;
  color: #999999;
  border: 2px solid #dedede;
  border-radius: 1px;
  -webkit-border-radius: 1px;
  transition: all 100ms ease-out;
}
.the-landscaper .esg-navigationbutton:hover {
  border-color: #cccccc;
}
.the-landscaper .esg-navigationbutton * { 
  color:#999999;
  transition: all 100ms ease-out;
}

.the-landscaper .esg-navigationbutton:hover * { color:#ffffff; }

.the-landscaper .esg-sortbutton-order.tp-desc:hover	{ color:#333333; }


.the-landscaper  .esg-sortbutton-wrapper,
.the-landscaper  .esg-cartbutton-wrapper { display:inline-block; }
.the-landscaper  .esg-sortbutton-order,
.the-landscaper  .esg-cartbutton-order {
  display:inline-block;
  vertical-align:top;
  border:none;
  width:40px;
  line-height:40px;
  font-size:12px;
  font-weight:700;
  color:#999;
  cursor: pointer;
  background:#eee;
  background: #fff;
  margin-left:5px;
									}

.the-landscaper .esg-cartbutton {	color:#fff;
								cursor: default !important;
							}
.the-landscaper .esg-cartbutton .esgicon-basket	{
  color:#fff;
  font-size:15px;
  line-height:15px;
  margin-right:10px;
											}
.the-landscaper .esg-cartbutton-wrapper { cursor: default !important; }

.the-landscaper .esg-sortbutton,
.the-landscaper .esg-cartbutton {
  display:inline-block;
  position:relative;
  cursor: pointer;
  margin-right:0px;
  border-radius:2px;
}

/********************************
	-	The Landscaper Home -
*********************************/
.the-landscaper-home .navigationbuttons,
.the-landscaper-home .esg-pagination,
.the-landscaper-home .esg-filters{
  text-transform:uppercase;
  text-align: center;
}

.the-landscaper-home .esg-filterbutton {
  position: relative;
}
.the-landscaper-home .esg-loadmore-wrapper {
  margin-top: 30px;
}

.the-landscaper-home .esg-filterbutton,
.the-landscaper-home .esg-sortbutton,
.the-landscaper-home .esg-cartbutton {
    display: block;
    padding: 15px 22px;
    color: #cecece;
    font-family: "Roboto", sans-serif;
    font-size: 15px;
    font-weight: bold;
    z-index: 2;
    text-transform: none;
    cursor: pointer;
    border-bottom: 1px solid rgba(0,0,0,.1);
    transition: all 100ms ease-in;
}

.the-landscaper-home .esg-navigationbutton.esg-left,
.the-landscaper-home .esg-navigationbutton.esg-right {
  border-color: #50524B;
}


@media(min-width: 992px) {
.the-landscaper-home .esg-filterbutton,
.the-landscaper-home .esg-sortbutton,
.the-landscaper-home .esg-cartbutton {
  position: relative;
  display: inline-block;
  border-right: 1px solid rgba(0,0,0,.1);
  margin-right: 7px;
  border: 2px solid #DCDCDC;
  color: #999999;
}
.the-landscaper-home .esg-filter-checked {
  padding: 1px 3px;
  color: #cbcbcb;
  background: #cbcbcb;
  margin-left: 7px;
  font-size: 9px;
  font-weight: 300;
  line-height: 9px;
  vertical-align: middle;
  }
}

.the-landscaper-home .esg-filterbutton.selected,
.the-landscaper-home .esg-filterbutton:hover {
  background-color: #A2C046;
  color: #ffffff;
  border-color: #A2C046;
}
.the-landscaper-home .esg-navigationbutton {
  position: relative;
  display: inline-block;
  padding: 13px 22px;
  font-family: "Roboto", sans-serif;
  font-weight: bold;
  cursor: pointer;
  color: #ffffff;
  background-color: #a2c046;
  -ms-transform: skew(-25deg);
  -webkit-transform: skew(-25deg);
  transform: skew(-25deg);
  transition: all 100ms ease-out;
}
.the-landscaper-home .esg-navigationbutton * {
  display: inline-block;
  -ms-transform: skew(25deg);
  -webkit-transform: skew(25deg);
  transform: skew(25deg);
}
.the-landscaper-home .esg-navigationbutton:hover {
  color:#ffffff;
  background-color: #98b63c;
}
.the-landscaper-home .esg-navigationbutton.esg-left:before,
.the-landscaper-home .esg-navigationbutton.esg-right::before {
  position: absolute;
  content: "";
  top: 0;
  width: 0;
  height: 0;
  border-style: solid;
  transition: all 100ms ease-out;
  -ms-transform: skew(25deg);
  -webkit-transform: skew(25deg);
  transform: skew(25deg);
}
.the-landscaper-home .esg-navigationbutton.esg-left {
  padding: 14px 22px 14px 11px;
  border-radius: 0 0 0 2px;
}
.the-landscaper-home .esg-navigationbutton.esg-right {
  padding: 14px 11px 14px 22px;
  border-radius: 0 2px 2px 0;
}

.the-landscaper-home .esg-navigationbutton.esg-left::before {
  left: -12px;
  border-width: 50px 48px 0 0;
  border-radius: 2px 0 0 2px;
  border-color: #a2c046 transparent transparent transparent;
}
.the-landscaper-home .esg-navigationbutton.esg-left:hover::before {
  border-color: #9ab83f transparent transparent transparent;
}
.the-landscaper-home .esg-navigationbutton.esg-right:before {
  right: -12px;
  border-width: 0 0 50px 48px;
  border-radius: 0 2px 2px 0;
  border-color: transparent transparent #a2c046 transparent;
}
.the-landscaper-home .esg-navigationbutton.esg-right:hover::before {
  border-color: transparent transparent #9ab83f transparent;
}
.the-landscaper-home .esg-navigationbutton.esg-right::after {
  position: absolute;
  content: "";
  top: 0;
  height: 48px;
  left: 0;
  border-left: 1px solid rgba(255, 255, 255, 0.1);
}



.the-landscaper-home .esg-sortbutton-order.tp-desc:hover {
  color:#333333;
}

.the-landscaper-home .esg-sortbutton-wrapper,
.the-landscaper-home .esg-cartbutton-wrapper {
  display:inline-block;
}
.the-landscaper-home .esg-sortbutton-order,
.the-landscaper-home .esg-cartbutton-order {
  display:inline-block;
  vertical-align:top;
  border:none;
  width:40px;
  line-height:40px;
  font-size:12px;
  font-weight:700;
  color:#999;
  cursor: pointer;
  background:#eee;
  background: #fff;
  margin-left:5px;
}

.the-landscaper-home .esg-cartbutton {
  color:#fff;
  cursor: default !important;
}
.the-landscaper-home .esg-cartbutton .esgicon-basket {
  color:#fff;
  font-size:15px;
  line-height:15px;
  margin-right:10px;
}
.the-landscaper-home .esg-cartbutton-wrapper {
  cursor: default !important;
}

.the-landscaper-home .esg-sortbutton,
.the-landscaper-home .esg-cartbutton {
  display:inline-block;
  position:relative;
  cursor: pointer;
  margin-right:0px;
  border-radius:2px;
}
